import React from 'react';

const ChangeBackground = (props) => {
  let weatherDescription = props.data.weather[0].description;
  let body = document.querySelector(body);
  switch (weatherDescription) {
    case 'clear sky':
      break;
    case 'few clouds':
      break;
    case 'scattered clouds':
      break;
    case 'broken clouds':
      break;
    case 'shower rain':
      break;
    case 'rain':
      break;
    case 'thunderstorm':
      break;
    case 'snow':
      break;
    case 'mist':
      break;
    default:
      break;
  }
  return null;
};

export default ChangeBackground;
