import React from 'react';
//displays the weather
const WeatherCard = (props) => {
  const getTemperature = () => {
    return props.data.main.temp;
  };
  const getWeatherType = () => {
    return props.data.weather[0].main;
  };
  const getWeatherDescription = () => {
    return props.data.weather[0].description;
  };
  const getWeatherIconUrl = () => {
    return `http://openweathermap.org/img/wn/${props.data.weather[0].icon}.png`;
  };

  const convertTemp = (temp) => {
    let fahrenheit = (temp - 273.15) * (9 / 5) + 32;
    let celcius = temp - 273.1;
    let temps = [fahrenheit, celcius]; // This could be state instead

    return Math.floor(fahrenheit * 100) / 100;
  };

  return (
    <div className="card">
      <h1>{props.data.name}</h1>
      <img alt="weather-icon" src={getWeatherIconUrl()}></img>
      <h3>{`${convertTemp(getTemperature())} °F`}</h3>
      <h4>{getWeatherType()}</h4>
      <h4>{getWeatherDescription()}</h4>
      <label class="switch">
        <input type="checkbox"></input>
        <span class="slider"></span>
      </label>
    </div>
  );
};

export default WeatherCard;
