import React, { useState } from 'react';
import FormDisplay from './FormDisplay';
import WeatherCard from './WeatherCard';
//Creates a form that user fills out location
const FormContainer = () => {
  const [weatherData, setWeatherData] = useState(null);

  const getWeatherData = async (location) => {
    try {
      const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=09cb87e3b19945bb6949563cd21ace23`; //need to change philadelphia to STATE
      const response = await fetch(url, { mode: 'cors' });
      const data = await response.json();
      setWeatherData(data);
      return data;
      // const temp = data.main.temp;
      //console.log(temp);
    } catch (err) {
      // need to fix this up more
      throw new Error(err);
    }
  };

  return <FormDisplay getWeatherData={getWeatherData} />;
};

export default FormContainer;
