import React, { useState } from 'react';
//displays the weather
const WeatherCard = (props) => {
  const roundTemp = (num) => {
    return Math.floor(num * 100) / 100;
  };

  let initialTemp = roundTemp(props.data.main.temp - 273.1);
  const [temperature, setTemp] = useState(initialTemp); // default temperature is in celcius
  const [scale, setScale] = useState('C');
  let location = props.data.name;
  // let temperature = props.data.main.temp;
  let weatherType = props.data.weather[0].main;
  let weatherDescription = props.data.weather[0].description;
  let weatherIconUrl = `http://openweathermap.org/img/wn/${props.data.weather[0].icon}.png`;

  const convertTemp = (e) => {
    let checked = e.target.checked;

    let temp = checked
      ? temperature * (9 / 5) + 32
      : (temperature - 32) * (5 / 9);
    temp = roundTemp(temp);
    //let fahrenheit = (temp - 273.15) * (9 / 5) + 32;
    //let celcius = temp - 273.1;
    //  let temps = [fahrenheit, celcius]; // This could be state instead
    // return Math.floor(temp * 100) / 100;

    setTemp(temp);
    scale === 'celcius' ? setScale('F') : setScale('C');
  };

  return (
    <div className="card">
      <h1>{location}</h1>
      <img alt="weather-icon" src={weatherIconUrl}></img>
      <h3>{`${temperature} °${scale}`}</h3>
      <h4>{weatherType}</h4>
      <h4>{weatherDescription}</h4>
      <label className="switch">
        <input
          type="checkbox"
          onChange={convertTemp}
          checked={scale === 'C'}
        ></input>
        <span className="slider"></span>
      </label>
    </div>
  );
};

export default WeatherCard;
