import React from 'react';

const ChangeBackground = (props) => {
  let weatherDescription = props.data.weather[0].description;
  let body = document.body;
  switch (weatherDescription) {
    case 'clear sky':
      body.style.background =
        "url('https://images.financialexpress.com/2020/04/sky660.jpg') no-repeat";
      body.style.backgroundSize = 'cover';
      break;
    case 'few clouds':
    case 'scattered clouds':
    case 'broken clouds':
      body.style.background =
        "url('https://images.pexels.com/photos/912110/pexels-photo-912110.jpeg?auto=compress&cs=tinysrgb&dpr=1') no-repeat";
      body.style.backgroundSize = 'cover';

    case 'shower rain':
      break;
    case 'rain':
      break;
    case 'thunderstorm':
      break;
    case 'snow':
      break;
    case 'mist':
      break;
    default:
      break;
  }
  return null;
};

export default ChangeBackground;
